import { Hono } from "hono";

type DuoApiError = {
  message?: string;
  type?: string;
};

type DuoUser = {
  name: string | null;
  username: string;
  streak: number;
  hasPlus: boolean;
  picture: string;
  languages?: Array<{
    learningLanguage: string;
    fromLanguage: string;
    points?: number;
    xp?: number;
  }>;
  courses?: Array<{
    learningLanguage: string;
    fromLanguage: string;
    points?: number;
    xp?: number;
  }>;
};

const app = new Hono();

async function fetchDuo<T>(url: string): Promise<T> {
  const res = await fetch(url);
  if (res.ok) {
    return res.json() as Promise<T>;
  }
  let message = `Duolingo API error (${res.status})`;
  try {
    const err: DuoApiError = await res.json();
    if (err?.message) {
      message = err.message;
    }
  } catch {
    // Ignore non-JSON formats
  }
  const error = new Error(message) as Error & {
    status?: number;
    type?: string;
  };
  error.status = res.status;
  throw error;
}

async function fetchDuolingoUser(username: string): Promise<DuoUser> {
  const data = await fetchDuo<{ users: DuoUser[] }>(
    `https://www.duolingo.com/2017-06-30/users?username=${username}`
  );
  const user = data.users && data.users.length > 0 ? data.users[0] : null;
  if (!user) {
    const error = new Error("User not found");
    (error as any).status = 404;
    throw error;
  }
  return user;
}

